package com.practice.jettrivia.componentimport android.util.Logimport androidx.compose.runtime.Composableimport com.practice.jettrivia.screens.QuestionsViewModel@Composablefun Questions(viewModel: QuestionsViewModel) {    val questions = viewModel.data.value.data?.toMutableList()    if (viewModel.data.value.loading == true) {        Log.d("LOADING", "Questions: Loading...")    } else {        Log.d("LOADING", "Questions: Loading STOPPED")        questions?.forEach { questionItem ->            Log.d("Result", "Questions: ${questionItem.question}")        }    }    Log.d("SIZE", "Questions: ${questions?.size}")}